package model;// 7. Model a Car, create an instance of it, pass it to System.out.println() so that its properties will//be printed.public class Car {    private String model;    private String color;    private int year;    private int doors;    public Car(String model, String color, int year, int doors) {        this.model = model;        this.color = color;        this.year = year;        this.doors = doors;    }    public static void main(String[] args) {        Car car1 = new Car("Ford","Red",1986, 4);        System.out.println(car1.model);        System.out.println(car1.color);        System.out.println(car1.year);        System.out.println(car1.doors);        System.out.println(car1);    }    public String getModel() {        return model;    }    public void setModel(String model) {        this.model = model;    }    public String getColor() {        return color;    }    public void setColor(String color) {        this.color = color;    }    public int getYear() {        return year;    }    public void setYear(int year) {        this.year = year;    }    public int getDoors() {        return doors;    }    public void setDoors(int doors) {        this.doors = doors;    }    @Override    public String toString() {        return "Car{" +                "model='" + model + '\'' +                ", color='" + color + '\'' +                ", year=" + year +                ", doors=" + doors +                '}';    }}